import Keys from "./keys";
import "./App.css";
import { useState, useEffect } from "react";
import Axios from "axios";
import CardWeather from "./shared/components/weather/CardWeather";
import WeatherInfo from "./shared/components/weather/WeatherInfo";
import CardAddress from "./shared/components/address/CardAddress";
import Spinner from "./shared/components/spinner/Spinner";
import locationService from "./shared/services/location.service";
import ILocation from "./shared/interfaces/location";

interface WeatherClass {
  main: {
    humidity: number;
    feels_like: number;
    temp: number;
    temp_max: number;
    temp_min: number;
    pressure: number;
  };
  wind: {
    speed: number;
  };
  sys: {
    sunrise: number;
    sunset: number;
  };
  dt: number;
}

const App = () => {
  const [address, setAddress] = useState<string>();
  const [weather, setWeather] = useState<WeatherClass>();

  const [loading, setLoading] = useState<boolean>(false);

  const [location, setLocation] = useState<ILocation>();

  useEffect(() => {
    locationService.getLocation()
  }, []);

  useEffect(() => {
      setLoading(true);

      if (location!.loaded) {
        Axios.get(
          "https://maps.googleapis.com/maps/api/geocode/json?latlng=" +
            location!.coords.lat +
            "," +
            location!.coords.lng +
            "&key=" +
            Keys.google
        ).then((response) => {
          setAddress(response.data.results[0].formatted_address);
        });
        Axios.get(
          "https://api.openweathermap.org/data/2.5/weather?lat=" +
            location!.coords.lat +
            "&lon=" +
            location!.coords.lng +
            "&appid=" +
            Keys.openWeather +
            "&lang=pt_br&units=metric"
        ).then((response) => {
          setWeather(response.data);

          setLoading(false);
        });
      }
  }, [location]);

  const refreshLocation = () => {
    setLocation({
      loaded: false,
      error: '',
      coords: {
        lat: 0,
        lng: 0,
      },
    })
  };
  return (
    <div className="App">
      <h1>Updated local weather</h1>

      <button onClick={refreshLocation}>Refresh</button>

      <CardAddress title="Address" address={address ? address : ""} />

      <div className="Cards">
        <CardWeather title="Local date time">
          <WeatherInfo
            title="Date"
            data={
              weather
                ? new Date(weather.dt * 1000).toLocaleDateString()
                : new Date().toLocaleDateString()
            }
          />
          <WeatherInfo
            title="Time"
            data={
              weather
                ? new Date(weather.dt * 1000).toLocaleTimeString()
                : new Date().toLocaleTimeString()
            }
          />
          <WeatherInfo
            title="Sunrise"
            data={
              weather
                ? new Date(weather.sys.sunrise * 1000).toLocaleTimeString()
                : new Date().toLocaleTimeString()
            }
          />
          <WeatherInfo
            title="Sunset"
            data={
              weather
                ? new Date(weather.sys.sunset * 1000).toLocaleTimeString()
                : new Date().toLocaleTimeString()
            }
          />
        </CardWeather>

        <CardWeather title="Temperature">
          <WeatherInfo
            title="Temperature"
            data={weather ? weather.main.temp.toFixed(0) : ""}
            metric=" 째C"
          />
          <WeatherInfo
            title="Minimum"
            data={weather ? weather.main.temp_min.toFixed(0) : ""}
            metric=" 째C"
          />
          <WeatherInfo
            title="Maximum"
            data={weather ? weather.main.temp_max.toFixed(0) : ""}
            metric=" 째C"
          />
          <WeatherInfo
            title="Thermal sensation"
            data={weather ? weather.main.feels_like.toFixed(0) : ""}
            metric=" 째C"
          />
        </CardWeather>

        <CardWeather title="Atmosphere">
          <WeatherInfo
            title="Humidity"
            data={weather ? weather.main.humidity.toString() : "0"}
            metric="%"
          />
          <WeatherInfo
            title="Pressure"
            data={weather ? weather.main.pressure.toString() : "0"}
            metric=" hPa"
          />
          <WeatherInfo
            title="Wind"
            data={weather ? (weather.wind.speed * 3.6).toFixed(0) : "0"}
            metric=" km/h"
          />
        </CardWeather>

        <Spinner loading={loading} />
      </div>
    </div>
  );
};

export default App;
